import React, { Component } from 'react';
import { Link } from "react-router-dom";
import { Container, Row, Col, Card, CardBody, Table } from "reactstrap";

//Import Breadcrumb
import Breadcrumbs from '../../components/Common/Breadcrumb';

//Import Image
import logo from "../../assets/images/logoMenor.png";
import api from "../../services/api"

class PedidosAndamentoDetalhe extends Component {

    state = {
        pedidosAndamento: []
    }
  

    async componentDidMount() {
        const response = await api.get(`/api/pedidos/get/${this.props.id}`)
        this.setState({ pedidosAndamento: response.data })
        console.log(response.data)
        
       
    }
    constructor() {
        super();
        this.printInvoice.bind(this);
    }

    //Print the Invoice
    printInvoice() {
        window.print();
    }

    render() {

        const { pedidosAndamento } = this.state;
        console.log(pedidosAndamento)
        return (
            <React.Fragment>
                <div className="page-content">
                    <Container fluid>

                    
                        <Row>
                            <Col lg="12">
                                {pedidosAndamento.map(pedido => (
                                    <Card>
                                        <CardBody>
                                            <div className="invoice-title">
                                                <h4 className="float-right font-size-16">ID # {pedido.id}</h4>
                                                <div className="mb-4">

                                                    <img src={logo} alt="logo" height="20" />
                                                Jovem Dev
                                            </div>
                                            </div>
                                            <hr />
                                            <Row>
                                                <Col xs="6">
                                                    <address>
                                                        <strong>Informações:</strong><br />
                                                    {pedido.pedido}<br />
                                                    {pedido.status}<br />
                                                    
                                                   
                                                </address>
                                                </Col>
                                                <Col xs="6" className="text-right">
                                                    <address>
                                                    <strong>Endereço:</strong><br />
                                                    {pedido.endereco}<br /><br />
                                                </address>
                                                </Col>
                                            </Row>
                                            <Row>
                                                <Col xs="12" className="mt-3">
                                                    <address>
                                                        <strong>Preço:</strong><br />
                                                        R${pedido.valor} <br />
                                                       
                                                </address>
                                                </Col>
                                               
                                            </Row>
                                            
                                          
                                            <div className="d-print-none">
                                                <div className="float-left">
                                                    <Link to="#" onClick={this.printInvoice} className="btn btn-success waves-effect waves-light mr-2"><i className="fa fa-print"></i></Link>
                                                    <Link to="#" className="btn btn-primary w-md waves-effect waves-light">Editar</Link>
                                                    
                                                </div>
                                            </div>
                                        </CardBody>
                                    </Card>
                                ))}
                            </Col>
                        </Row>

                    </Container>
                </div>
            </React.Fragment>
        );
    }
}

export default PedidosAndamentoDetalhe;
