              {
                date: "04 Mar, 2020",
                type: "Buy",
                coin: "Bitcoin",
                value: "0.00321 BTC	",
                valueinUSD: "$ 1802.62",
                status : <span className="badge badge-warning font-size-10">Pending</span>
              },
              {
                date: "05 Mar, 2020",
                type: "Buy",
                coin: "Litecoin",
                value: "0.00224 LTC",
                valueinUSD: "$ 1773.01",
                status : <span className="badge badge-success font-size-10">Completed</span>
              },
              {
                date: "06 Mar, 2020",
                type: "Buy",
                coin: "Ethereum",
                value: "Ethereum",
                valueinUSD: "$ 9423.73",
                status : <span className="badge badge-danger font-size-10">Failed</span>
              },
              {
                date: "07 Mar, 2020",
                type: "Buy",
                coin: "Bitcoin",
                value: "1.00952 BTC",
                valueinUSD: "$ 9067.62",
                status : <span className="badge badge-success font-size-10">Completed</span>
              },
              {
