import React, { Component } from 'react';
import { Container, Row, Col } from "reactstrap";

//Import Components
import FeatureBox from "./feature-box";

//Import images
import feature1 from "../../../../assets/images/crypto/features-img/img-1.png";
import feature2 from "../../../../assets/images/crypto/features-img/img-2.png";

class Features extends Component {
    constructor(props) {
        super(props);
        this.state = {
            features1 : [
                { id : 1, desc : "Donec pede justo vel aliquet" },
                { id : 2, desc : "Aenean et nisl sagittis" },
            ],
            features2 : [
                { id : 1, desc : "Donec pede justo vel aliquet" },
                { id : 2, desc : "Aenean et nisl sagittis" },
            ]
        }
    }

    render() {
        return (
            <React.Fragment>
                <section className="section" id="features">
                    <Container>
                        <Row>
                            <Col lg="12">
                                <div className="text-center mb-5">
                                    <div className="small-title">Features</div>
                                    <h4>Key features of the product</h4>
                                </div>
                            </Col>
                        </Row>
                        

                        <Row className="align-items-center pt-4">
                            <Col md="6" sm="8">
                                <div>
                                    <img src={feature1} alt="" className="img-fluid mx-auto d-block"/>
                                </div>
                            </Col>
                            <Col md="5" className="ml-auto">
                                {/* featurebox */}
                                <FeatureBox num="01" title="Lending" features={this.state.features1} desc="If several languages coalesce, the grammar of the resulting language is more simple and regular than of the individual will be more simple and regular than the existing." />
                            </Col>
                        </Row>
                        

                        <Row className="align-items-center mt-5 pt-md-5">
                            <Col md="5">
                                {/* featurebox */}
                                <FeatureBox num="02" title="Wallet" features={this.state.features2} desc="It will be as simple as Occidental; in fact, it will be Occidental. To an English person, it will seem like simplified English, as a skeptical Cambridge friend." />
                            </Col>
                            <Col md="6" sm="8" className="ml-md-auto">
                                <div className="mt-4 mr-md-0">
                                    <img src={feature2} alt="" className="img-fluid mx-auto d-block"/>
                                </div>
                            </Col>
                        </Row>
                    </Container>
                </section>
            </React.Fragment>
        );
    }
}

export default Features;
